import React,{useState} from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import styled from "styled-components";
import LocationOnIcon from '@material-ui/icons/LocationOn';
import PhoneIcon from '@material-ui/icons/Phone';
import MailIcon from '@material-ui/icons/Mail';
import BgImage from './_assets/bg2.jpeg';




const MainDiv = styled.main`
// background-color:gray;
margin-top:45px;
`;


//card 1

const MainCard = styled(Card)`
margin-bottom:30px;
margin-left:220px;
// margin-right:400px;
margin-top:30px;
float:left;
width:500px;
`;

const ContentDiv = styled.div`
margin-top:120px;
`;

const CardContentDiv = styled(CardContent)`
    background-color:#f1f1f1;
    background-image: url(${BgImage});
    height:550px;
`;



const TypographyDiv = styled(Typography)`
`;



const TextDiv = styled.h4`
color:white;
`;


const LocationOnIconDiv = styled(LocationOnIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
const PhoneIconDiv = styled(PhoneIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
const MailIconDiv = styled(MailIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
 const Ptag = styled(Typography)`
 color:#E5E4E2;
 margin-left: 67px!important;
 `;

 const Ptag2 = styled(Typography)`
 color:#00FF00;
 margin-left: 67px!important;
 `;

 const Ptag3 = styled(Typography)`
 color:#00FF00;
 margin-left: 67px!important;
 `;

 //card 2

 const ContentDiv1 = styled.div`
margin-top:60px;

`;
 const TextDiv1 = styled.h4`
color:darkslategray;
`;

const MainCard2 = styled(Card)`
margin-bottom:30px;
// margin-left:400px;
margin-right:220px;
margin-top:30px;
width:500px;
float:right;
`;

const CardContentDiv1 = styled(CardContent)`
    // background-color:#EFFBFB;
    height:550px;

`;

 //user form

 const Main = styled.main`
  max-width: 350px;
  margin-left: 40px ;
  width: 100%;
  @media (max-width: 768px) {
    max-width: 300px;
  }
`;
const Formcontrol = styled(FormControl)`
min-width:280px;
`;
const Form = styled.div`
  color: red;
  align-items: center;

`;
const SubmitButton = styled.button`
  width:400px;
  color:white;
  background-color:	darkslategray;
  padding :10px; 
  border-radius:10px;
  cursor:pointer;
`;
const InputStyle = styled(TextField)`
width:400px;
`
const Break = styled.div`
margin-bottom:20px;
`

function ContactPage(props) {

    const [email, setEmail] = useState('');
    const [name,setName] = useState("");
    const [number,setNumber] = useState("");
    const [message,setMessage] = useState("")

    const handleSubmit = e => {
        e.preventDefault();
        let data={
        email,
        name,
        number,
        message
        }
       console.log(data)
    };


    return (
        <MainDiv>
            <MainCard >
                <CardContentDiv>
                    <ContentDiv> </ContentDiv>
                    <TypographyDiv>
                        <LocationOnIconDiv /><TextDiv>Address</TextDiv>
                    </TypographyDiv>
                    <Ptag>
                        Veerannapalya,Bangalore,India.
                    </Ptag>
                    <TypographyDiv>
                    <PhoneIconDiv /><TextDiv>Lets Talk</TextDiv>
                    </TypographyDiv>
                    <Ptag2>
                     +91 8886257181
                    </Ptag2>
                    <TypographyDiv>
                    <MailIconDiv /><TextDiv>Contat Me?</TextDiv>
                    </TypographyDiv>
                    <Ptag2>
                     sethukumartirumanyam@gmail.com
                    </Ptag2>

                </CardContentDiv>
            </MainCard>





            <MainCard2 >
                <CardContentDiv1>
                <ContentDiv1> </ContentDiv1>
                    <TypographyDiv>
                        <center><TextDiv1>Send Me A Message ?</TextDiv1></center>
                    </TypographyDiv>
                    <Main>
                <Form>
                    <form onSubmit={handleSubmit}>
                        <Formcontrol>
                            {/* <InputLabel htmlFor="email">Email Address</InputLabel> */}
                            <InputStyle
                                id="name"
                                name="name"
                                autoComplete="name"
                                value={name}
                                onChange={e => setName(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Full Name"
                            />
                            <Break/>
                               <InputStyle
                                id="email"
                                name="email"
                                autoComplete="email"
                                value={email}
                                onChange={e => setEmail(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Email"
                            />
                            <Break/>
                               <InputStyle
                                id="number"
                                name="number"
                                autoComplete="number"
                                value={number}
                                onChange={e => setNumber(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Phone Number"
                            />
                            <Break/>
                               <InputStyle
                                id="message"
                                name="message"
                                autoComplete="message"
                                value={message}
                                onChange={e => setMessage(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Message"
                                multiline
                                rows={4}
                            />
                            <Break/>
                            <center><SubmitButton type="submit">Submit</SubmitButton></center>
                        </Formcontrol>
                    </form>
                </Form>
            </Main>
                </CardContentDiv1>
            </MainCard2>
        </MainDiv>
    );
}

export default ContactPage;
