import React,{useState} from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import styled from "styled-components";
import LocationOnIcon from '@material-ui/icons/LocationOn';
import PhoneIcon from '@material-ui/icons/Phone';
import MailIcon from '@material-ui/icons/Mail';
import BgImage from './_assets/bg2.jpeg';


const MainDiv = styled.main`
// background-color:gray;
background-color:red
`;

const MainCard = styled(Card)`
margin-bottom:30px;
margin-left:220px;
// margin-right:400px;
margin-top:30px;
float:left;
width:500px;
`;
const ContentDiv = styled.div`
margin-top:120px;
`;

const CardContentDiv = styled(CardContent)`
    background-color:#f1f1f1;
    background-image: url(${BgImage});
    height:550px;
`;

const CardContentDiv1 = styled(CardContent)`
    background-color:#E5E4E2;
`;

const TypographyDiv = styled(Typography)`
`;

const MainCard2 = styled(Card)`
margin-bottom:30px;
// margin-left:400px;
margin-right:220px;
margin-top:30px;
width:500px;
float:right;
`

const TextDiv = styled.h4`
color:white;
`

const LocationOnIconDiv = styled(LocationOnIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
const PhoneIconDiv = styled(PhoneIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
const MailIconDiv = styled(MailIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
 const Ptag = styled(Typography)`
 color:#E5E4E2;
 margin-left: 67px!important;

 `
 const Ptag2 = styled(Typography)`
 color:#00FF00;
 margin-left: 67px!important;

 `
 const Ptag3 = styled(Typography)`
 color:#00FF00;
 margin-left: 67px!important;

 `

 //user form

 const Main = styled.main`
  max-width: 350px;
  margin-left: 70px ;
  width: 100%;
  @media (max-width: 768px) {
    max-width: 300px;
  }
`;
const Formcontrol = styled(FormControl)`
min-width:280px;
`;
const Form = styled.div`
  color: red;
  align-items: center;

`;
const SubmitButton = styled.button`
  width:400px;
  color:white;
  background-color:	darkslategray;
  padding :8px; 
  border-radius:10px;
  cursor:pointer;
`;
const InputStyle = styled(TextField)`
width:400px;
`
const Break = styled.div`
margin-bottom:20px;
`

function ContactPage(props) {

    const [email, setEmail] = useState('');
    const [values, setValues] = React.useState({ password: '' })

    const handleSubmit = e => {
        e.preventDefault();
        let data={
        email,
        password : values.password,
        }
        props.onSubmit(data)

    };
    const handleChange = prop => event => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
        setValues({ ...values, showPassword: !values.showPassword });
    };

    const handleMouseDownPassword = event => {
        event.preventDefault();
    };

    return (
        <MainDiv>
            <MainCard >
                <CardContentDiv>
                    <ContentDiv> </ContentDiv>
                    <TypographyDiv>
                        <LocationOnIconDiv /><TextDiv>Address</TextDiv>
                    </TypographyDiv>
                    <Ptag>
                        Veerannapalya,Bangalore,India.
                    </Ptag>
                    <TypographyDiv>
                    <PhoneIconDiv /><TextDiv>Lets Talk</TextDiv>
                    </TypographyDiv>
                    <Ptag2>
                     +91 8886257181
                    </Ptag2>
                    <TypographyDiv>
                    <MailIconDiv /><TextDiv>Contat Me?</TextDiv>
                    </TypographyDiv>
                    <Ptag2>
                     sethukumartirumanyam@gmail.com
                    </Ptag2>

                </CardContentDiv>
            </MainCard>





            <MainCard2 >
                <CardContentDiv1>
                    <TypographyDiv>
                        <TextDiv>Send Me A Message</TextDiv>
                    </TypographyDiv>
                    <Main>
                <Form>
                    <form onSubmit={handleSubmit}>
                        <Formcontrol>
                            {/* <InputLabel htmlFor="email">Email Address</InputLabel> */}
                            <InputStyle
                                id="email"
                                name="email"
                                autoComplete="email"
                                value={email}
                                onChange={e => setEmail(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Full Name"
                            />
                            <Break/>
                               <InputStyle
                                id="email"
                                name="email"
                                autoComplete="email"
                                value={email}
                                onChange={e => setEmail(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Email"
                            />
                            <Break/>
                               <InputStyle
                                id="email"
                                name="email"
                                autoComplete="email"
                                value={email}
                                onChange={e => setEmail(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Phone Number"
                            />
                            <Break/>
                               <InputStyle
                                id="email"
                                name="email"
                                autoComplete="email"
                                value={email}
                                onChange={e => setEmail(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Message"
                            />
                            <Break/>
                            <center><SubmitButton type="submit">Submit</SubmitButton></center>
                        </Formcontrol>
                    </form>
                </Form>
            </Main>
                </CardContentDiv1>
            </MainCard2>
        </MainDiv>
    );
}

export default ContactPage;
