import React,{useState} from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import styled from "styled-components";
import LocationOnIcon from '@material-ui/icons/LocationOn';
import PhoneIcon from '@material-ui/icons/Phone';
import MailIcon from '@material-ui/icons/Mail';
import BgImage from './_assets/bg2.jpeg';


const MainDiv = styled.main`
// background-color:gray;
background-color:red
`;

const MainCard = styled(Card)`
margin-bottom:30px;
margin-left:220px;
// margin-right:400px;
margin-top:30px;
float:left;
width:500px;
`;
const ContentDiv = styled.div`
margin-top:120px;
`;

const CardContentDiv = styled(CardContent)`
    background-color:#f1f1f1;
    background-image: url(${BgImage});
    height:550px;
`;

const CardContentDiv1 = styled(CardContent)`
    background-color:#808080;
`;

const TypographyDiv = styled(Typography)`
`;

const MainCard2 = styled(Card)`
margin-bottom:30px;
// margin-left:400px;
margin-right:220px;
margin-top:30px;
width:500px;
float:right;
`

const TextDiv = styled.h4`
color:white;
`

const LocationOnIconDiv = styled(LocationOnIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
const PhoneIconDiv = styled(PhoneIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
const MailIconDiv = styled(MailIcon)`
color:white;
float:left;
margin-top:2px;
margin-left:4px;
margin-right: 40px;
`;
 const Ptag = styled(Typography)`
 color:#E5E4E2;
 margin-left: 67px!important;

 `
 const Ptag2 = styled(Typography)`
 color:#00FF00;
 margin-left: 67px!important;

 `
 const Ptag3 = styled(Typography)`
 color:#00FF00;
 margin-left: 67px!important;

 `

function ContactPage(props) {

    const [email, setEmail] = useState('');
    const [values, setValues] = React.useState({ password: '' })

    const handleSubmit = e => {
        e.preventDefault();
        let data={
        email,
        password : values.password,
        }
        props.onSubmit(data)

    };
    const handleChange = prop => event => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
        setValues({ ...values, showPassword: !values.showPassword });
    };

    const handleMouseDownPassword = event => {
        event.preventDefault();
    };

    return (
        <MainDiv>
            <MainCard >
                <CardContentDiv>
                    <ContentDiv> </ContentDiv>
                    <TypographyDiv>
                        <LocationOnIconDiv /><TextDiv>Address</TextDiv>
                    </TypographyDiv>
                    <Ptag>
                        Veerannapalya,Bangalore,India.
                    </Ptag>
                    <TypographyDiv>
                    <PhoneIconDiv /><TextDiv>Lets Talk</TextDiv>
                    </TypographyDiv>
                    <Ptag2>
                     +91 8886257181
                    </Ptag2>
                    <TypographyDiv>
                    <MailIconDiv /><TextDiv>Contat Me?</TextDiv>
                    </TypographyDiv>
                    <Ptag2>
                     sethukumartirumanyam@gmail.com
                    </Ptag2>

                </CardContentDiv>
            </MainCard>





            <MainCard2 >
                <CardContentDiv1>
                    <TypographyDiv>
                        <TextDiv>Send Me A Message</TextDiv>
                    </TypographyDiv>
                    <Main>
                <Form>
                    <form onSubmit={handleSubmit}>
                        <Formcontrol>
                            {/* <InputLabel htmlFor="email">Email Address</InputLabel> */}
                            <InputStyle
                                id="email"
                                name="email"
                                autoComplete="email"
                                value={email}
                                onChange={e => setEmail(e.target.value)}
                                variant="outlined"
                                id="outlined-basic" label="Email"
                            />
                        </Formcontrol>
                        <Break />
                        <Formcontrol>
                            {/* <InputLabel htmlFor="standard-adornment-password">Password</InputLabel> */}
                            <InputStyle
                                type={values.showPassword ? 'text' : 'password'}
                                value={values.password}
                                onChange={handleChange('password')}
                                endAdornment={
                                    <InputAdornment position="end">
                                        <IconButton
                                            aria-label="toggle password visibility"
                                            onClick={handleClickShowPassword}
                                            onMouseDown={handleMouseDownPassword}
                                        >
                                            {values.showPassword ? <Visibility /> : <VisibilityOff />}
                                        </IconButton>
                                    </InputAdornment>
                                }
                                variant="outlined"
                                id="outlined-basic" label="Password"
                            />
                        </Formcontrol>
                        <Break />
                        <center><SubmitButton type="submit">Login</SubmitButton></center>
                        <Break />
                        <center><p style={{color:"black"}}>Not Yet Registerd ? <b><a href="/register" style={{color:"black"}}>Register</a></b></p></center>
                        <Break />
                        <center><p style={{color:"black"}}>Back to home ? <b><a href="/" style={{color:"black"}}>Home</a></b></p></center>

                    </form>
                </Form>
            </Main>
                </CardContentDiv1>
            </MainCard2>
        </MainDiv>
    );
}

export default ContactPage;
